import { useState } from 'react';
import '../index.css';


const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState('');
  const [nameTouched, setNameTouched] = useState(false);

  const nameValid = enteredName.trim() !== '';
  const invalid = !nameValid && nameTouched;

  let formIsValid = false;


  if (nameValid) {
    formIsValid = true;
  } else {
    formIsValid = false;
  }

  const nameInputChangeHandler = (event) => {
    setEnteredName(event.target.value);
  };
  const nameInputBlurHandler = (event) => {
    setNameTouched(true);
  }

  const formSubmissionHandler = (event) => {
    event.preventDefault();
    setNameTouched(true);
    if (!nameValid) {
      return;
    }

    console.log(enteredName);
    setEnteredName('');
    setNameTouched(false);
  };


  const namedInputClasses = !invalid ? 'form-control' : 'form-control invalid';

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={namedInputClasses}>
        <label htmlFor='name'>Your Name</label>
        <input
          type='text'
          id='name'
          onChange={nameInputChangeHandler}
          onBlur={nameInputBlurHandler}
          value={enteredName}
        />
        {invalid && <p className="error-text">Name must not be empty.</p>}
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
