import { useState } from 'react';
import '../index.css';


const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState('');
  const [enteredEmail, setEnteredEmail] = useState('');
  const [nameTouched, setNameTouched] = useState(false);
  const [emailTouched, setEmailTouched] = useState(false);


  const nameInputValid = enteredName.trim() !== '';

  const emailInputValid = enteredEmail.includes("@") && enteredEmail.includes('.');

  const nameInvalid = (!nameInputValid && nameTouched);
  const emailInvalid = (!emailInputValid && emailTouched);

  let formIsValid = false;


  if (nameInputValid && emailInputValid) {
    formIsValid = true;
  } else {
    formIsValid = false;
  }

  const nameInputChangeHandler = (event) => {
    setEnteredName(event.target.value);
  };
  const nameInputBlurHandler = () => {
    setNameTouched(true);
  }

  const emailInputChangeHandler = (event) => {
    setEnteredEmail(event.target.value);
  }

  const emailInputBlurHandler = () => {
    setEmailTouched(true);
  }

  const formSubmissionHandler = (event) => {
    event.preventDefault();
    setNameTouched(true);
    setEmailTouched(true);
    if (!nameInputValid || !emailInputValid) {
      return;
    }

    setEnteredName('');
    setEnteredEmail('');
    setNameTouched(false);
    setEmailTouched(false);
  };


  const namedInputClasses = !nameInvalid ? 'form-control' : 'form-control invalid';
  const emailInputClasses = !emailInvalid ? 'form-control' : 'form-control invalid';

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={namedInputClasses}>
        <label htmlFor='name'>Your Name</label>
        <input
          type='text'
          id='name'
          onChange={nameInputChangeHandler}
          onBlur={nameInputBlurHandler}
          value={enteredName}
        />
        {nameInvalid && <p className="error-text">Name must not be empty.</p>}
      </div>

      <div className={emailInputClasses}>
        <label htmlFor='email'>Your Email</label>
        <input
          type='email'
          id='email'
          onChange={emailInputChangeHandler}
          onBlur={emailInputBlurHandler}
          value={enteredEmail}
        />
        {emailInvalid && <p className="error-text">Email is invalid.</p>}
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
